// MovieRecommendations.js

import React, { useState } from 'react';
import { getRecommendations } from './api'; // Import the getRecommendations function

const MovieRecommendations = () => {
  const [userPreferences, setUserPreferences] = useState('');
  const [recommendations, setRecommendations] = useState([]);

  const handleRecommendations = async () => {
    try {
      const preferencesArray = userPreferences.split(',').map(preference => preference.trim());
      const recommendations = await getRecommendations(preferencesArray);
      setRecommendations(recommendations);
    } catch (error) {
      // Handle the error here if needed
    }
  };

  return (
    <div style={{ background: 'lightpink', padding: '20px' }}>
      <h2 style={{ color: 'lightaqua', marginBottom: '20px' }}>Movie Recommendations</h2>
      <input
        type="text"
        value={userPreferences}
        onChange={(e) => setUserPreferences(e.target.value)}
        placeholder="Enter your preferences (comma-separated)"
        style={{ padding: '8px', marginRight: '10px' }}
      />
      <button onClick={handleRecommendations} style={{ background: 'lightaqua', padding: '8px', color: 'white', border: 'none', cursor: 'pointer' }}>Get Recommendations</button>

      {/* Display the movie recommendations */}
      <ul>
        {recommendations.map((movie, index) => (
          <li key={index} style={{ color: 'lightaqua' }}>{movie}</li>
        ))}
      </ul>
    </div>
  );
};

export default MovieRecommendations;
