//This component will be used to input user preferences
//We can create a form with input fields for movie genres

import React, { useState } from 'react';

const MovieForm = ({ onSubmit }) => {
  const [userPreferences, setUserPreferences] = useState('');

  const handleInputChange = (event) => {
    setUserPreferences(event.target.value);
  };

  const handleSubmit = (event) => {
    event.preventDefault();
    onSubmit(userPreferences);
  };

  return (
    <form onSubmit={handleSubmit}>
      <label htmlFor="userPreferences">Enter your movie preferences:</label>
      <input
        type="text"
        id="userPreferences"
        value={userPreferences}
        onChange={handleInputChange}
      />
      <button type="submit">Get Recommendations</button>
    </form>
  );
};

export default MovieForm;
