import React, { useState } from 'react';
import { getRecommendations } from '../api';

const MovieRecommendations = () => {
  alert('Component loaded!')
  const genres = [
    'Drama',
    'Crime',
    'Action',
    'Adventure',
    'Romance',
    'Sci-Fi',
    'Thriller',
    // Add more genres as needed
  ];

  const [selectedGenre, setSelectedGenre] = useState('');
  const [recommendations, setRecommendations] = useState([]);

  const handleGetRecommendations = async () => {
    try {
      const response = await getRecommendations([selectedGenre]);
      setRecommendations(response);
    } catch (error) {
      // Handle the error here if needed
    }
  };

  return (
    <div className="container mt-5">
      <h1 className="mb-4">Movie Recommendations</h1>
      <div className="input-group mb-3">
        <div className="input-group-prepend">
          <button
            className="btn btn-outline-secondary dropdown-toggle"
            type="button"
            data-toggle="dropdown"
            aria-haspopup="true"
            aria-expanded="false"
          >
            {selectedGenre ? selectedGenre : 'Select Genre'}
          </button>
          <div className="dropdown-menu">
            {genres.map((genre) => (
              <button
                key={genre}
                className="dropdown-item"
                type="button"
                onClick={() => setSelectedGenre(genre)}
              >
                {genre}
              </button>
            ))}
          </div>
        </div>
        <input
          type="text"
          className="form-control"
          value={selectedGenre}
          onChange={(e) => setSelectedGenre(e.target.value)}
          placeholder="Enter your preferences (or use the dropdown)"
        />
        <button className="btn btn-primary" onClick={handleGetRecommendations}>
          Get Recommendations
        </button>
      </div>

      {/* Display the movie recommendations */}
      <ul className="list-group">
        {recommendations.map((movie, index) => (
          <li key={index} className="list-group-item">
            {movie}
          </li>
        ))}
      </ul>

      <p className="mt-4 text-muted">Powered by Movie Recommendation API</p>
    </div>
  );
};

export default MovieRecommendations;
