// App.js
import React, { useState } from 'react';
import MovieForm from './components/MovieForm';
import MovieList from './components/MovieList';
import MovieRecommendations from './components/MovieRecommendations';
import axios from 'axios';


const App = () => {
  const [recommendedMovies, setRecommendedMovies] = useState([]);

  const getMovieRecommendations = (userPreferences) => {
    // Call your API here to get movie recommendations
    axios.get(`http://localhost:8080/api/api/recommendations?userPreferences=${userPreferences}`)
      .then((response) => {
        setRecommendedMovies(response.data);
      })
      .catch((error) => {
        console.error('Error fetching movie recommendations:', error);
      });
  };

  return (
    <div>
      <h1>Movie Recommendation SPA</h1>
      <MovieForm onSubmit={getMovieRecommendations} />
      <MovieList recommendedMovies={recommendedMovies} />
    </div>
  );
};

export default App;
